import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const HeaderStyled = styled.header`
  background: url('') no-repeat;
  background-size: cover;
  background-position: center;
  color: #fff;
  padding: 1mm;
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media (max-width: 800px) {
    flex-direction: column;
    align-items: flex-start;
  }
`;

const Title = styled(Link)`
  width: 30%; // ajusta este valor según sea necesario
  margin: 0;
  margin-right: 6em; // añadir margen a la derecha
  color: #ffffff; // cambiar color de texto a blanco
  text-decoration: none;
  display: flex; // esto centra el texto en la caja
  align-items: center;
  justify-content: center;
  background: url('https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/NaltonColage.png') repeat center / 40% 130%; // agregar la imagen de fondo
  padding: 5px;
  font-size: 3em; // modifica el tamaño de la fuente del título aquí
  font-family: 'Helvetica Neue', sans-serif; // cambiar el tipo de letra
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5); // sombra de texto
  border-radius: 10px; // bordes redondeados
  background-color: #d3d3d3; // color de fondo gris claro
`;

const Navigation = styled.nav`
  display: flex;
  gap: 1em;
  flex: 1; // esto permitirá que la navegación tome todo el espacio restante después del título
  height: 100%; // añadir esto para asegurar que los elementos de navegación llenen la altura
  font-size: 1.5em; 

  @media (max-width: 800px) {
    width: 100%;
    justify-content: space-evenly;
  }
`;

const StyledLink = styled(Link)`
  display: inline-block;
  background: rgba(0, 0, 0, 0.6); // dar un poco de opacidad
  border-radius: 30px; // bordes redondeados
  padding: 20px 40px; // relleno para hacerlo grande
  font-size: 1.25em; // tamaño de texto
  font-family: 'Helvetica Neue', sans-serif; // cambiar el tipo de letra
  font-weight: 300; // hacer el peso de la fuente más ligero
  color: #FFFFFF; // color de texto
  text-shadow: 1px 1px 2px #000000; // sombra de texto
  cursor: pointer;
  text-decoration: none;
  z-index: 1;
  margin-left: -20px; // mover un poco a la izquierda
  margin-top: 20px; // mover un poco hacia abajo
  transition: background 0.3s ease, color 0.3s ease; // animación suave del cambio de color de fondo y texto
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2); // sombra de caja para darle profundidad
  &:hover {
    color: ${props => props.hoverColor || "#FFFFFF"};
    background: rgba(255, 255, 255, 0.6); // cambiar color de fondo a rojo cuando se pasa el cursor
  }
`;

const TeamMember = styled.button`
  background-color: transparent;
  border: none;
  text-align: center;
  cursor: pointer;
  transition: transform 0.3s ease;
  
  &:hover {
    transform: scale(1.1);
  }
`;

const MemberImage = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  object-fit: cover;
`;

const MemberName = styled.h2`
  color: #ffffff;
  font-size: 1.5em;
  margin-top: 0.5em;
`;

const MemberRole = styled.p`
  color: #ffffff;
  font-size: 1.2em;
`;

const TeamMemberComponent = ({ name, role, image, to }) => (
  <StyledLink to={to}>
    <TeamMember>
      <MemberImage src={image} alt={name} />
      <MemberName>{name}</MemberName>
      <MemberRole>{role}</MemberRole>
    </TeamMember>
  </StyledLink>
);

const TeamSection = styled.section`
  display: flex;
  justify-content: space-evenly;
  padding: 2em 0;
  background-color: #000;
`;

const TeamSectionComponent = ({ teamMembers }) => (
  <TeamSection>
    {teamMembers.map((member, index) => (
      <TeamMemberComponent 
        key={index} 
        name={member.name} 
        role={member.role} 
        image={member.image} 
        to={member.to}
      />
    ))}
  </TeamSection>
);


function Header() {
  return (
    <HeaderStyled>
      <Title to="/">NALTON HOME</Title>
      <Navigation>
        <StyledLink to="/about" hoverColor="green">About</StyledLink>
        <StyledLink to="/ecosystem">Ecosystem</StyledLink>
        <StyledLink to="/launch" hoverColor="red">Launch App</StyledLink>
      </Navigation>
    </HeaderStyled>
  );
}

export default Header;
