import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Link } from "react-router-dom";

const BodyStyled = styled.main`
  flex: 1;
  color: #000;
  padding: 1mm;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  gap: 1em;
  position: relative; 
`;

const BackgroundImage = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-image: url(${props => props.backgroundImage});
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  opacity: 0.4;
`;

const Title = styled.h1`
  margin: 0;
  z-index: 1;
  color: #FFFFFF; // color de texto
  font-size: 5em; // tamaño de texto
  text-shadow: 2px 2px 4px #000000; // sombra de texto
`;

const Description = styled.p`
  max-width: 800px;
  font-size: 1.2em; 
  font-style: italic; // estilo de texto
  font-weight: bold; // peso de texto
  z-index: 1;
  color: #FFFFFF; // color de texto
  text-shadow: 1px 1px 2px #000000; // sombra de texto
`;

const LaunchButton = styled(Link)`
  display: inline-block;
  background: url('https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/LaunchAppGif.gif') no-repeat center;
  background-size: contain;
  background-position: center;
  width: calc(1.4 * 20vw * 250 / 258); 
  height: calc(1.4 * 20vw);
  text-decoration: none;
  z-index: 1;
`;

const backgroundImages = [
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/LogP1.png",
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/LogP2.png",
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/LogP3.png",
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/LogP4.png"
];

const bubbleImages = [
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/Ripple1.png",
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/Ripple2.png",
  "https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/Ripple3.png"
];


const Bubble = styled.div`
  position: absolute;
  background-image: url(${props => props.image});
  background-position: center;
  background-repeat: no-repeat;
  background-size: contain;
  width: 100px;
  height: 100px;
  top: ${props => props.top}%;
  left: ${props => props.left}%;
  cursor: pointer;
  z-index: 10;
`;


function Body() {
  const [backgroundImage, setBackgroundImage] = useState(backgroundImages[0]);
  const [bubbles, setBubbles] = useState(
    Array(5).fill().map((_, i) => ({
      image: bubbleImages[i % bubbleImages.length], // se selecciona una imagen de burbuja basándose en el índice
      id: i,
      top: Math.random() * 80, // posiciones aleatorias
      left: Math.random() * 80, // posiciones aleatorias
    }))
  );

  useEffect(() => {
    let i = 0;
    const interval = setInterval(() => {
      i = i === backgroundImages.length - 1 ? 0 : i + 1;
      setBackgroundImage(backgroundImages[i]);
    }, 3000);
    return () => {
      clearInterval(interval);
    };
  }, []);

  const handleBubbleClick = (id) => {
    setBubbles(bubbles.filter(bubble => bubble.id !== id));
  };

  return (
    <BodyStyled>
      <BackgroundImage backgroundImage={backgroundImage} />
      <Title>Nalton Paiy App</Title>
      <LaunchButton to="/launch"></LaunchButton>
      <Description>
        It is a Latin American financial content generator. Using Artificial Intelligence, users can make payments, send remittances, generate NFT images of their purchases, obtain loans and more under a multi-chain CBDC ecosystem on XRP Ledger.
      </Description>
      {bubbles.map(bubble => (
        <Bubble
          key={bubble.id}
          image={bubble.image}
          top={bubble.top}
          left={bubble.left}
          onClick={() => handleBubbleClick(bubble.id)}
        />
      ))}
    </BodyStyled>
  );
}

export default Body;
