import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';

const HeaderStyled = styled.header`
  background: url('https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/Ensigna (1).png') no-repeat;
  background-size: cover;
  background-position: center;
  color: #fff;
  padding: 1mm;
  display: flex;
  justify-content: space-between;
  align-items: center;

  @media (max-width: 800px) {
    flex-direction: column;
    align-items: flex-start;
  }
`;

const Title = styled(Link)`
  width: 50%; // ajusta este valor según sea necesario
  margin: 0;
  color: #000000;
  text-decoration: none;
  display: flex; // esto centra el texto en la caja
  align-items: center;
  justify-content: center;
  background-color: rgba(0, 0, 0, 0); // sin opacidad
  padding: 0px;
  font-size: 3em; // modifica el tamaño de la fuente del título aquí
  // agrega más estilos según sea necesario
`;

const Navigation = styled.nav`
  display: flex;
  gap: 1em;
  flex: 1; // esto permitirá que la navegación tome todo el espacio restante después del título
  height: 100%; // añadir esto para asegurar que los elementos de navegación llenen la altura
  font-size: 1.5em; 

  @media (max-width: 800px) {
    width: 100%;
    justify-content: space-evenly;
  }
`;

const StyledLink = styled(Link)`
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  text-decoration: none;
  flex: 1; // Esto hará que los enlaces ocupen partes iguales del espacio restante
  background-color: rgba(0, 0, 0, 0.5); // opacidad
  height: 100%; // añadir esto para asegurar que los enlaces llenen la altura

  &:hover {
    color: ${props => props.hoverColor || "#fff"}; // cambiar color en vez de text-decoration-color
  }
`;

function Header() {
  return (
    <HeaderStyled>
      <Title to="/">NALTON HOME</Title>
      <Navigation>
        <StyledLink to="/about" hoverColor="green">About</StyledLink>
        <StyledLink to="/ecosystem">Ecosystem</StyledLink>
        <StyledLink to="/launch" hoverColor="red">Launch App</StyledLink>
      </Navigation>
    </HeaderStyled>
  );
}

export default Header;
