import React, { useEffect } from "react";
import styled from "styled-components";
import Spline from '@splinetool/react-spline'; 
import { Link } from 'react-router-dom';

// Añade tu estilo personalizado al componente de la Sección
const Section = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const BodyStyled = styled.main`
  position: relative; 
  flex: 1;
  color: #000;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  text-align: center;
  gap: 2em;
`;

const ContentWrapper = styled.div`
  z-index: 1; // Añade esto
  padding: 2em;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1em;
`;


const SplineBackground = styled(Spline)`
  position: absolute; 
  top: 0; 
  right: 0; 
  bottom: 0;
  left: 0;
  z-index: 0; 
`;


const Title = styled.h1`
  margin: 0 0 1em; 
  color: #FFFFFF; 
  font-size: 5em;
  text-shadow: 2px 2px 4px #000000;
`;

const Description = styled.p`
  max-width: 800px;
  font-size: 1.2em; 
  font-style: italic; 
  font-weight: bold; 
  color: #FFFFFF;
  text-shadow: 1px 1px 2px #000000; 
`;

const StyledLink = styled(Link)`
  display: inline-block;
  background: rgba(0, 0, 0, 0.6); 
  border-radius: 50px;
  padding: 20px 50px; 
  font-size: 1.5em; 
  font-weight: bold; 
  color: #FFFFFF; 
  text-shadow: 1px 1px 2px #000000; 
  cursor: pointer;
  text-decoration: none;
  margin-left: -26px; 
  margin-top: 20px; 
  transition: background 0.3s ease;
  &:hover {
    color: ${props => props.hoverColor || "#FFFFFF"};
    background: rgba(255, 0, 128, 0.6);
  }
`;

const TeamMember = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 1.5em;
  width: 150px;  
`;

const TeamSection = styled.section`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  padding: 2em 0;
  background-color: #000;
  width: 100%;
  z-index: 2;  // Ajustar a 2
`;


const MemberImage = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  object-fit: cover;
`;

const MemberName = styled.h2`
  color: #ffffff;
  font-size: 1.5em;
  margin-top: 0.5em;
`;

const MemberRole = styled.p`
  color: #ffffff;
  font-size: 1.2em;
  margin: 0;
  text-align: center;
`;

const TeamMemberComponent = ({ name, role, image, to, isExternal }) => (
  isExternal ?
  <a href={to} target="_blank" rel="noopener noreferrer">
    <TeamMember>
      <MemberImage src={image} alt={name} />
      <MemberName>{name}</MemberName>
      <MemberRole>{role}</MemberRole> 
    </TeamMember>
  </a> :
  <StyledLink to={to}>
    <TeamMember>
      <MemberImage src={image} alt={name} />
      <MemberName>{name}</MemberName>
      <MemberRole>{role}</MemberRole> 
    </TeamMember>
  </StyledLink>
);


const TeamSectionComponent = ({ teamMembers }) => (
  <TeamSection>
    {teamMembers.map((member, index) => (
      <TeamMemberComponent 
        key={index} 
        name={member.name} 
        role={member.role} 
        image={member.image} 
        to={member.to}
      />
    ))}
  </TeamSection>
);

const teamMembers = [
  { name: 'Daniel', role: 'Product Manager', image: 'https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/T1.png', to: '/daniel-info' },
  { name: 'Jose', role: 'Developer', image: 'https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/T2.png', to: 'https://github.com/JoseEmmanuelVG', isExternal: true },
  { name: 'Oscar', role: 'Project Manager', image: 'https://raw.githubusercontent.com/bancambios/PaiyApp-Hackaton-Etherfuse/main/Images/T3.png', to: '/oscar-info' },
];


function Body() {
  useEffect(() => {
    const preloader = document.createElement('img');
    preloader.src = 'URL_DE_TU_ANIMACIÓN'; 
    preloader.style.display = 'none';
    document.body.appendChild(preloader);
  }, []);

  return (
    <BodyStyled>
      <SplineBackground scene="https://prod.spline.design/M7AzHLSZ0vlxSGs9/scene.splinecode" /> 
      <Section>
        <ContentWrapper>
          <Title>Nalton Paiy App</Title>
          <StyledLink to="/launch" hoverColor="black">Test the App</StyledLink>
          <Description>
            It is a Latin American financial content generator. Using Artificial Intelligence, users can make payments, send remittances, generate NFT images of their purchases, obtain loans and more under a multi-chain CBDC ecosystem on XRP Ledger.
          </Description>
        </ContentWrapper>
      </Section>
      <Section>
        <TeamSectionComponent teamMembers={teamMembers} />
      </Section>
    </BodyStyled>
  );
}

export default Body;
