import React, { useEffect } from "react";
import styled from "styled-components";
import Spline from '@splinetool/react-spline'; 
import { Link } from 'react-router-dom';

const Section = styled.section`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const ContentWrapper = styled.div`
  z-index: 1;
  padding: 2em;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1em;
`;

const Title = styled.h1`
  margin: 0 0 1em; 
  color: #FFFFFF; 
  font-size: 5em;
  text-shadow: 2px 2px 4px #000000;
`;

const Description = styled.p`
  max-width: 800px;
  font-size: 1.2em; 
  font-style: italic; 
  font-weight: bold; 
  color: #FFFFFF;
  text-shadow: 1px 1px 2px #000000; 
`;

const StyledLink = styled(Link)`
  display: inline-block;
  background: rgba(0, 0, 0, 0.6); 
  border-radius: 50px;
  padding: 20px 50px; 
  font-size: 1.5em; 
  font-weight: bold; 
  color: #FFFFFF; 
  text-shadow: 1px 1px 2px #000000; 
  cursor: pointer;
  text-decoration: none;
  margin-left: -26px; 
  margin-top: 20px; 
  transition: background 0.3s ease;
  &:hover {
    color: ${props => props.hoverColor || "#FFFFFF"};
    background: rgba(255, 0, 128, 0.6);
  }
`;

const TopSectionBackground = styled.div`
  position: relative; 
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
`;

const SplineBackground = styled(Spline)`
  position: absolute; 
  top: 0; 
  right: 0; 
  bottom: 0;
  left: 0;
  z-index: 0;
`;

function TopSection() {
  useEffect(() => {
    const preloader = document.createElement('img');
    preloader.src = 'https://prod.spline.design/M7AzHLSZ0vlxSGs9/scene.splinecode'; 
    preloader.style.display = 'none';
    document.body.appendChild(preloader);
  }, []);

  return (
    <TopSectionBackground>
      <SplineBackground scene="https://prod.spline.design/M7AzHLSZ0vlxSGs9/scene.splinecode" /> 
        <ContentWrapper>
          <Title>Nalton Paiy App</Title>
          <StyledLink to="/launch" hoverColor="black">Test the App</StyledLink>
          <Description>
            It is a Latin American financial content generator. Using Artificial Intelligence, users can make payments, send remittances, generate NFT images of their purchases, obtain loans and more under a multi-chain CBDC ecosystem on XRP Ledger.
          </Description>
        </ContentWrapper>
    </TopSectionBackground>
  );
}

export default TopSection;
